// Assuming 'AggregatedData' is the name of the node with the aggregated data
const aggregatedData = $node["Aggregate"].json;

// Prepare the message
let message = 'Hi Team,\n\nNew time entries are ready for review. Here are the details:\n\n';

const dates = [...new Set(aggregatedData.s2e7d2b64e)];

dates.forEach(date => {
  message += `*Date:* ${date}\n`;
  message += `*Client:* ${aggregatedData.s388b58221[0]}\n`;
   message += '\n\n';
  message += '```\n';  // Start block quote for grey frame
  message += 'Title                      | Status               | Worked Hours  | Description\n';
  message += '---------------------------|----------------------|---------------|----------------------------------------------\n';

  aggregatedData.s2e7d2b64e.forEach((entryDate, index) => {
    if (entryDate === date) {
      const title = aggregatedData.title[index].padEnd(27, ' ');
      const status = aggregatedData.status[index].value.padEnd(20, ' ');
      const hours = aggregatedData.se6d1375cc[index].toString().padEnd(13, ' ');
      const description = aggregatedData.text[index];
      message += `${title}| ${status} | ${hours} | ${description}\n`;
    }
  });
  message += '```\n\n';  // End block quote for grey frame
});

message += '\nPlease review these entries at your earliest convenience.\nThank you!\n';

return [
  {
    json: {
      message: message
    }
  }
];

