  var n8nWorkflow = $input.all()[1].json;

  // Order JSON Objects
  var orderedOriginal = {}
  var orderedActual = {}

  Object.keys(origWorkflow).sort().forEach(function(key) {
    orderedOriginal[key] = origWorkflow[key];
  });

  Object.keys(n8nWorkflow).sort().forEach(function(key) {
    orderedActual[key] = n8nWorkflow[key];
  });

  // Determine Difference
  if ( JSON.stringify(orderedOriginal) === JSON.stringify(orderedActual) ) {
    $input.all()[0].json.github_status = "same";
    $input.all()[0].json.content_decoded = orderedOriginal;
  } else {
    $input.all()[0].json.github_status = "different";
    $input.all()[0].json.content_decoded = orderedOriginal;
    $input.all()[0].json.n8n_data_stringy = JSON.stringify(orderedActual, null, 2);
  }
// No File Returned / New Workflow
} else {
  // Order JSON Object
  var n8nWorkflow = ($input.all()[1].json);
  var orderedActual = {}
  Object.keys(n8nWorkflow).sort().forEach(function(key) {
    orderedActual[key] = n8nWorkflow[key];
  });

  // Proper Formatting
  $input.all()[0].json.github_status = "new";
  $input.all()[0].json.n8n_data_stringy = JSON.stringify(orderedActual, null, 2);
}

